const{Mark:d}=Statamic.$bard.tiptap.core,n=d.create({name:"customId",addAttributes(){return{id:{default:null,parseHTML:t=>t.getAttribute("id"),renderHTML:t=>t.id?{id:t.id}:{}}}},parseHTML(){return[{tag:"span[data-custom-id]"}]},renderHTML({HTMLAttributes:t}){return["span",{"data-custom-id":t.id},0]},addCommands(){return{setCustomId:t=>({chain:e})=>e().setMark(this.name,{id:t}).run(),unsetCustomId:()=>({chain:t})=>t().unsetMark(this.name).run()}}});Statamic.$bard.addExtension(()=>n);Statamic.$bard.buttons((t,e)=>e({name:"custom_id",text:__("Add ID"),svg:"tag",command:r=>{const a=prompt("Enter ID for the selected text:");a&&r.chain().focus().setCustomId(a).run()},active:r=>r.isActive("customId")}));
